"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[9],{5313:e=>{e.exports=JSON.parse('{"title":"Asana Workflows","description":"Asana is a project management tool designed to help teams organize, track, and manage their work. Teams can create projects, assign tasks, set deadlines, and communicate about specific assignments. Asana workflows refer to the automated sequences within Asana that streamline processes by reducing manual task entry and oversight. Workflows can be customized to trigger specific actions based on predefined rules. The following guides cover the basics of creating a workflow for deadline-bound projects.","slug":"/category/asana-workflows","permalink":"/KershiaMukoro.github.io/docs/category/asana-workflows","navigation":{"previous":{"title":"Sample How-to Guides","permalink":"/KershiaMukoro.github.io/docs/category/sample-how-to-guides"},"next":{"title":"Workflow Basics","permalink":"/KershiaMukoro.github.io/docs/How-to/Asana Workflows/basics"}}}')}}]);